import React, {FC} from "react";
import {Autocomplete} from "@material-ui/lab";
import {CustomFormikField} from "@src/components/elements/custom_formik_field/CustomFormikField";


const locations = [
    {
        id: 11,
        name: "Ташкентская область",
        cities: [
            {
                id: 150,
                name: "Паркентский район",
                district: []
            },
            {
                id: 151,
                name: "Пскентский район",
                district: []
            },
            {
                id: 152,
                name: "Город Ташкент",
                district: [
                    {
                        id: 1,
                        city_id: 152,
                        name: "Бектемирский район"
                    },
                    {
                        id: 2,
                        city_id: 152,
                        name: "Мирзо-Улугбекский район"
                    },
                    {
                        id: 3,
                        city_id: 152,
                        name: "Мирабадский район"
                    },
                ]
            },
            {
                id: 153,
                name: "Уртачирчикский район",
                district: []
            },
            {
                id: 154,
                name: "Чиназский район",
                district: []
            }
        ]
    },
    {
        id: 12,
        name: "Ферганская область",
        cities: [
            {
                id: 158,
                name: 'Бешарыкский район',
                district: []
            },
            {
                id: 159,
                name: 'Багдадский район',
                district: []
            },
            {
                id: 160,
                name: 'Бувайдинский район',
                district: []
            },
            {
                id: 161,
                name: 'Дангаринский район',
                district: [
                    {
                        id: 3,
                        city_id: 152,
                        name: "Юнусабадский район"
                    }
                ]
            },
        ]
    }
];

const option = ({area, city, district}) => {
    return `${district ? `${district}, ` : ''}${city}, ${area}`;
};

const formattedData = locations.reduce((acc, row) => {
    row.cities.forEach((city) => {
        if (city.district.length) {
            city.district.forEach((dis) => {
                acc = [
                    ...acc,
                    {
                        area: row.name,
                        city: city.name,
                        district: dis.name
                    }
                ];
            });
        } else {
            acc = [
                ...acc,
                {
                    area: row.name,
                    city: city.name
                }
            ];
        }
    });
    return acc;
}, []);

export const CustomAutocomplete: FC<{placeholder: string, name: string}> = (props) => {

    return (
        <Autocomplete
            options={formattedData}
            getOptionLabel={option}
            renderInput={(params) => (
                <CustomFormikField {...params} {...props} />
            )}
        />
    )
};
