import React from "react";
import {Slide, useScrollTrigger} from "@material-ui/core";


export const withScroll = (Component, threshold) => {
    const isThreshold = useScrollTrigger({disableHysteresis: true, threshold});
    return (
        <Component isThreshold={isThreshold}/>
    );
};
