import React from "react";
import {useScrollTrigger} from "@material-ui/core";


export const withScrollThreshold = (Component, threshold) => {
    const isThreshold = useScrollTrigger({disableHysteresis: true, threshold});
    return (
        <div>
            <Component isThreshold={isThreshold}/>
        </div>
    );
};
