import React from "react";
import {SliderArrow} from "@src/components/elements/slider_arrow/SliderArrow";


export const settings = {
    dots: false,
    infinite: true,
    // autoplay: true,
    // arrows: false,
    centerMode: true,
    slidesToShow: 3,
    slidesToScroll: 1,
    // variableWidth: true,
    speed: 500,
    autoplaySpeed: 5000,
    prevArrow: <SliderArrow/>,
    nextArrow: <SliderArrow type='next'/>,
    responsive: [
        {
            breakpoint: 992,
            settings: {
                slidesToShow: 2,
                centerMode: false,
                centerPadding: "0px"
            }
        },
        {
            breakpoint: 768,
            settings: {
                slidesToShow: 1,
            }
        }
    ]
};
