import {AnyAction} from "redux"
import {HYDRATE} from "next-redux-wrapper"
import {IErrors} from "../../../interfaces/IErrors"
import {errorActionTypes} from '../actions/errorsActions'

const initialState = {
    authError: null
};

export const errors = (state: IErrors = initialState, action: AnyAction): IErrors => {
    switch (action.type) {
        case HYDRATE:
            return {
                ...state,
                ...action.payload.errors
            }
        case errorActionTypes.AUTH_ERROR:
            return {
                ...state,
                ...action.payload
            }
        default:
            return state;
    }
};
