export const dataPrepare = (data: any) => {
    return Object.keys(data).reduce((acc, key) => {
        if (Array.isArray(data[key])) {
            acc[key] = data[key];
            data[key].map(innerList => {
                if (Array.isArray(innerList) && innerList.length) {
                    acc[innerList] = innerList;
                }
            })
        }
        console.log(acc)
        return acc
    }, {})
}
