import {isRequired} from "@root/validation_schemas/createAdvrtSchema";
import {Grid, Typography} from "@material-ui/core";
import {CustomCheckbox} from "@src/components/elements/custom_checkbox/CustomCheckbox";
import {CustomMenu} from "@src/components/elements/custom_menu/CustomMenu";
import React, {FC} from "react";
import {useStyles} from "./useStyles";


export const AdvrtParams: FC<any> = (props) => {
    const {
        list,
        errors,
        touched,
        isPreview,
        onBlur,
        values,
        handleMenuItem,
        handleListItem,
        handleParamsCheckbox,
    } = props;

    const {key} = list;
    const {adParams} = values;

    let fields;

    // if (isRequired(key) || (key === 'adsParams' && list.data[0].condition && list.data[0].condition.length)) {
    //     if (key !== 'adsParams') {
    //         initReqFields[key] = {id: null, name: 'Не выбрано'};
    //     } else {
    //         key = 'condition';
    //         list.data = list.data[0].condition;
    //         initReqFields[key] = list.data[0];
    //     }
    // }
    const classes = useStyles({isPreview});

    const isExcludedRows = key !== 'id' && key !== 'name' && key !== 'adsParams';

    const isOptions = key === 'safety'
        || key === 'multimedia'
        || key === 'assistant'
        || key === 'exterior';

    const isSpecialRows = key === 'body' || key === 'colors' || isOptions;

    if (isExcludedRows && list.data) {
        if (isSpecialRows && list.data.length) {
            fields = (
                <>
                    <Typography variant="subtitle1">
                        {key}
                        {
                            isRequired(key)
                            && <span className='error-text'>*</span>
                        }
                    </Typography>
                    <div className='row-list'>
                        {
                            list.data.map(item => (
                                <div key={item.id}>
                                    {
                                        key === 'body'
                                            ? <div
                                                onClick={!isPreview ? handleListItem(key, item) : null}
                                                className={
                                                    adParams[key]
                                                    && adParams[key].id === item.id
                                                        ? classes.selected : ''
                                                }
                                            >
                                                <img src={item.icon.url} alt={item.name}/>
                                                <Typography>{item.name}</Typography>
                                            </div>
                                            : <>
                                                {
                                                    key === 'colors'
                                                        ? <div
                                                            onClick={!isPreview ? handleListItem(key, item) : null}
                                                            className={adParams[key] && adParams[key].id === item.id ? classes.selected : ''}
                                                            style={{
                                                                width: '50px',
                                                                height: '50px',
                                                                backgroundColor: `${item.hex_color_code}`,
                                                            }}
                                                        />
                                                        : <div style={{display: 'flex', alignItems: 'center'}}>
                                                            <CustomCheckbox
                                                                disabled={isPreview}
                                                                checked={
                                                                    adParams[key]
                                                                    && adParams[key].some(val => val.id === item.id)
                                                                }
                                                                onChange={handleParamsCheckbox(key, item)}
                                                            />
                                                            <Typography>{item.name}</Typography>
                                                        </div>
                                                }
                                            </>
                                    }
                                </div>
                            ))
                        }
                    </div>
                </>
            );
        } else {
            if (!Array.isArray(list.data)) {
                fields = (
                    <>
                        <Typography variant="subtitle1">
                            {key}
                            {
                                isRequired(key)
                                && <span className='error-text'>*</span>
                            }
                        </Typography>
                        {
                            key === 'body'
                                ? <Typography>{list.data.name}</Typography>
                                : key === 'colors'
                                ? <div
                                    style={{
                                        width: '50px',
                                        height: '50px',
                                        backgroundColor: `${list.data.hex_color_code}`
                                    }}
                                />
                                : <Typography>{list.data.name}</Typography>
                        }
                    </>
                );
            } else if (list.data.length) {
                if (key !== 'condition') list.data = [{id: null, name: 'Не выбрано'}, ...list.data];
                fields = (
                    <>
                        <Typography variant="subtitle1">
                            <strong>
                                {key}
                                {
                                    isRequired(key)
                                    && <span className='error-text'>*</span>
                                }
                            </strong>
                        </Typography>
                        <CustomMenu
                            className={
                                errors.adParams
                                && errors.adParams[key]
                                && touched[key] ? 'error-border' : ''
                            }
                            name={key}
                            onBlur={onBlur}
                            items={list.data}
                            valueName={adParams[key] ? adParams[key].name : list.data[0].name}
                            onClick={handleMenuItem(key)}
                        />
                    </>
                );
            }
        }
        return (
            <Grid
                item
                container
                className={classes.gridItem}
                key={key}
                xs={12}
                sm={isSpecialRows && !isPreview || isOptions ? 12 : 6}
            >
                {fields}
            </Grid>
        )
    } else {
        return <></>
    }
}
