import {userApi} from '../../api/api'

export const mainSliderActionsTypes = {
    FETCH_REQUEST: 'FETCH_REQUEST',
    GET_SUCCESS: 'GET_SUCCESS',
    GET_ERROR: 'GET_ERROR'
}

export const fetchRequest = () => ({
    type: mainSliderActionsTypes.FETCH_REQUEST
});
export const getSuccess = imgs => ({
    type: mainSliderActionsTypes.GET_SUCCESS,
    imgs
});
export const getError = errors => ({
    type: mainSliderActionsTypes.GET_ERROR,
    errors
});

export const fetchMainSliderImgs = () => {
    return async (dispatch) => {
        dispatch(fetchRequest());
        try {
            const imgs = await userApi.getMainSliderImgs();
            return dispatch(getSuccess(imgs));
        } catch (e) {
            return dispatch(getError([e]));
        }
    };
};
