import React, {FC} from 'react';
import {Typography} from "@material-ui/core";
import {WithT} from "i18next";
import {CustomSlider} from '@src/components/elements/custom_slider/CustomSlider';
import {CardItem} from '@src/components/elements/card/Card';
import {useStyles} from './useStyles';


export const AncmntsSlider: FC<WithT & { title: string, list: any }> = (props) => {
    const {t, list, title} = props;

    const classes = useStyles();
    return (
        <div className={classes.root}>
            <Typography className='title' variant='h2'>{title}</Typography>
            <div className='slider'>
                <CustomSlider
                    slidesToShow={4}
                    slidesToScroll={4}
                    infinite={false}
                >
                    {list.map((card, i) => (
                        <CardItem key={i} {...card} t={t}/>
                    ))}
                </CustomSlider>
            </div>
        </div>
    )
};
