import React, {FC} from "react"
import {TextField} from "@material-ui/core"
import {Field, FieldValidator} from "formik"

interface Props {
    name: string,
    type: string,
    placeholder: string,
    className: string
    setFieldValue: (field: string, value: any, shouldValidate?: boolean) => void,
    validate: FieldValidator,
}

export const CustomField: FC<Props> = ({setFieldValue, validate, ...props}) => {

    const handler = ({target}) => {
        const {value} = target;

        setFieldValue(props.name, value);
    };

    return (
        <Field validate={validate} name={props.name}>
            {({field}) => {
                return (
                    <TextField
                        fullWidth
                        focused={false}
                        variant='outlined'
                        onChange={handler}
                        {...props}
                        {...field}
                    />
                )
            }}
        </Field>
    )
};
