export const dataPrepare = (data, adParams) => {
    return Object.keys(data).reduce((acc, key) => {
        if (Array.isArray(data[key]) && key !== 'adsParams') {
            acc[key] = data[key];
            acc = {
                ...acc,
                ...dataPrepare(adParams, {})
            };
            // adParams[key].map(innerKey => {
            //     acc = {...acc, ...dataPrepare(innerKey, adParams)};
            // })
        }
        console.log(acc)
        return acc;
    }, {})
}
