import React, {FC, useState} from 'react';
import {Grid, Hidden, Container, Typography} from '@material-ui/core';
import {AdvrtForm} from './advrtForm/AdvrtForm';
import {SuccessAdvrt} from './successAdvrt/SuccessAdvrt';
import {AdvertisementProps} from "@root/interfaces/Advertisement";
import {createAdvrtSchema} from "@root/validation_schemas/createAdvrtSchema";
import {Form, Formik} from "formik";
import {useSelector} from "react-redux";
import {RootState} from "@src/redux/rootReducer";
import {ButtonComponent} from "@src/components/elements/button/Button";
import {MainLayout} from "@src/components/MainLayout";
// styles
import {useStyles} from './useStyles';


export const CreateAdvrt: FC<AdvertisementProps> = () => {
    const {createAdvrt} = useSelector((store: RootState) => store);

    const [isPreview, setIsPreview] = useState(false);
    const [isSuccess, setIsSuccess] = useState(false);


    const handlePreview = (value) => () => {
        setIsPreview(value)
    };

    const initVals = {
        title: '',
        safe_deal: false,
        delivery: false,
        exchange: false,
        location: '',
        files: [],
        description: '',
        phone: '',
        adsParams: {}
    };

    const submit = () => {
        isPreview
            ? console.log('submit')
            : setIsPreview(true)
    };
    console.log(createAdvrtSchema)
    const classes = useStyles();
    return (
        <MainLayout>
            <Container maxWidth="lg">
                <Grid container spacing={1}>
                    <Grid item xs={12} md={9}>
                        <Formik
                            initialValues={initVals}
                            validationSchema={createAdvrtSchema}
                            onSubmit={submit}
                        >
                            {
                                ({
                                     errors,
                                     touched,
                                     values,
                                     setValues,
                                     handleBlur,
                                     setErrors
                                 }) => {
                                    // console.log(values)
                                    console.log(errors)

                                    const isValid = createAdvrtSchema.validateSync(values);
                                    console.log(isValid)

                                    const handleCheckboxChange = (valName) => ({target}) => {
                                        setValues({...values, [valName]: target.checked});
                                    };

                                    const handleClickMenuItem = (valueName) => (newValue, setAnchor) => () => {
                                        setAnchor(null);

                                        setValues({
                                            ...values,
                                            adsParams: {
                                                ...values.adsParams,
                                                [valueName]: newValue
                                            }
                                        });

                                        // Reset sub props in values
                                        Object.keys(newValue).map(key => {
                                            if (values.adsParams[key]) {
                                                setValues({
                                                    ...values,
                                                    adsParams: {
                                                        ...values.adsParams,
                                                        [valueName]: newValue,
                                                        [key]: {id: null, name: 'Не выбрано', ...newValue[key]}
                                                    }
                                                })
                                            }
                                        });
                                    };

                                    const resetValues = () => {
                                        setValues(initVals);
                                    };

                                    return (
                                        <Form>
                                            {
                                                isSuccess
                                                    ? <SuccessAdvrt/>
                                                    : (
                                                        <AdvrtForm
                                                            isPreview={isPreview}
                                                            setIsPreview={setIsPreview}
                                                            createAdvrt={createAdvrt}
                                                            errors={errors}
                                                            touched={touched}
                                                            values={values}
                                                            resetValues={resetValues}
                                                            handleBlur={handleBlur}
                                                            handleClickMenuItem={handleClickMenuItem}
                                                            handleCheckboxChange={handleCheckboxChange}
                                                        />
                                                    )
                                            }
                                            <div className={classes.nextButtonBlock}>
                                                {
                                                    isPreview && (
                                                        <ButtonComponent
                                                            className={classes.nextButton}
                                                            onClick={handlePreview(false)}
                                                        >
                                                            <Typography>
                                                                Назад
                                                            </Typography>
                                                        </ButtonComponent>
                                                    )
                                                }
                                                <ButtonComponent
                                                    type='submit'
                                                    className={classes.nextButton}
                                                >
                                                    <Typography>
                                                        {
                                                            isPreview
                                                                ? 'Создать'
                                                                : 'Далее'
                                                        }
                                                    </Typography>
                                                </ButtonComponent>
                                            </div>
                                        </Form>
                                    )
                                }
                            }
                        </Formik>
                    </Grid>
                    <Hidden smDown>
                        <Grid
                            item
                            md={3}
                            container
                            justify="flex-end"
                            className={classes.adBanner}
                        >
                            <Grid item md={12}>
                                <div className="right-banner"/>
                            </Grid>
                        </Grid>
                    </Hidden>
                </Grid>
            </Container>
        </MainLayout>
    )
}
