import {createAsyncThunk, createSlice} from '@reduxjs/toolkit';
import {AdOrLot} from '@root/interfaces/AdOrLot';
import {userAPI} from "@src/api/api";
import {CategoryIDs} from "@root/interfaces/Categories";


const initialState: AdOrLot = {
    isFetch: false,
    error: null,
    adType: {
        id: null,
        name: 'Выберите тип объявления'
    },
    category: {
        id: null,
        name: null,
        childs: []
    },

};

export const fetchAd = createAsyncThunk<any, CategoryIDs>(
    'adOrLot/fetchAd',
    async ({ctgryID, subCtgryID, lang}, {rejectWithValue}) => {
        try {
            return await userAPI.getAdOrLot(ctgryID, subCtgryID, lang);
        } catch (e) {
            return rejectWithValue(e.message);
        }
    }
);

const createAdOrLotSlice = createSlice({
    name: 'adOrLot',
    initialState,
    reducers: {
        setAdOrLotCtgryAction: (state, action) => {
            state.adType = action.payload.adType;
            state.category = action.payload.category;
        }
    },
    extraReducers: (builder) => {
        builder.addCase(fetchAd.pending, (state) => {
            state.isFetch = true;
            state.error = null;
        })
        builder.addCase(fetchAd.fulfilled, (state, action) => {
            state.isFetch = false;
            state.adType = action.payload;
        })
        builder.addCase(fetchAd.rejected, (state, action) => {
            state.isFetch = false;
            state.error = action.payload;
        })
    }
});

export const {setAdOrLotCtgryAction} = createAdOrLotSlice.actions;
export const createAdOrLotReducer = createAdOrLotSlice.reducer;
