import {number, boolean, object} from "yup";

export const adTypeAndCtgrySchema = object({
    type: object({
        isLot: number().nullable().required('Выберите тип объявления!')
    }),
    category: object({
        id: boolean().nullable().required('Выберите категорию!')
    })
})
