import React, {useState} from "react"
import {Formik, Form} from "formik"
import {useRouter} from "next/router"
import {MenuItem, Typography} from "@material-ui/core"
import {ButtonComponent} from "@src/components/elements/button/Button"
import {object, number} from 'yup'
import {useStyles} from './useStyles'
import {CustomFormikMenu} from "@src/components/elements/custom_formik_menu/CustomFormikMenu"

const initValues = {
    type: {
        id: null,
        desc: 'Выберите тип',
    },
    category: {
        id: null,
        desc: 'Выберите категорию'
    }
};

export const CreateAdModalForm = (props) => {
    const {handleCloseModal} = props;

    const router = useRouter();

    const [typeEl, setTypeEl] = useState(null);
    const [estateEl, setEstateEl] = useState(null);

    const validationSchema = object({
        type: object({
            id: number().nullable().required('Выберите тип объявления!')
        }),
        category: object({
            id: number().nullable().required('Выберите категорию!')
        })
    });

    const handleMenuOpen = (anchor) => (e) => {
        anchor(e.currentTarget)
    };

    const handleMenuClose = (anchor) => () => {
        anchor(null)
    };

    const handleClickMenuItem = (handleSetValue, values, newValue) => () => {
        handleSetValue({...values, ...newValue});
    };

    const submit = () => {
        router.push('/create_advertisement');
    };

    const classes = useStyles();
    return (
        <Formik initialValues={initValues} validationSchema={validationSchema} onSubmit={submit}>
            {(props) => {
                const {
                    errors,
                    touched,
                    values,
                    setValues,
                    setTouched,
                    handleBlur,
                    handleChange
                } = props;
                console.log(values)
                // console.log(touched)
                return (
                    <Form className={classes.root}>
                        <div>
                            <Typography>{errors.type}</Typography>
                            <ButtonComponent onClick={handleMenuOpen(setTypeEl)}>{values.type.desc}</ButtonComponent>
                            <CustomFormikMenu
                                name={values}
                                anchorEl={typeEl}
                                open={Boolean(typeEl)}
                                onClose={handleMenuClose(setTypeEl)}
                                setValues={setValues}
                            >
                                <MenuItem
                                    value={1}
                                    id='Объявление'
                                    onClick={handleClickMenuItem(setValues, values, {
                                        type: {
                                            id: 1,
                                            desc: 'Объявление'
                                        }
                                    })}
                                    onBlur={handleBlur}
                                >
                                    Объявление
                                </MenuItem>
                                <MenuItem
                                    value={2}
                                    id='Торг'
                                    onClick={handleClickMenuItem(setValues, values, {
                                        type: {
                                            id: 2,
                                            desc: 'Торг'
                                        }
                                    })}
                                    onBlur={handleBlur}
                                >
                                    Торг
                                </MenuItem>
                            </CustomFormikMenu>
                        </div>
                        <div>
                            <Typography>{errors.category}</Typography>
                            <ButtonComponent onClick={handleMenuOpen(setEstateEl)}>{values.category.desc}</ButtonComponent>
                            <CustomFormikMenu
                                name={values}
                                anchorEl={estateEl}
                                open={Boolean(estateEl)}
                                onClose={handleMenuClose(setEstateEl)}
                                setValues={setValues}
                            >
                                <MenuItem
                                    id='Недвижимость'
                                    value={3}
                                    onBlur={handleBlur}
                                    onClick={handleClickMenuItem(setValues, values, {
                                        category: {
                                            id: 3,
                                            desc: 'Недвижимость'
                                        }
                                    })}
                                >
                                    Недвижимость
                                </MenuItem>
                                <MenuItem
                                    id='Автомобили'
                                    value={4}
                                    onBlur={handleBlur}
                                    onClick={handleClickMenuItem(setValues, values, {
                                        category: {
                                            id: 4,
                                            desc: 'Автомобили'
                                        }
                                    })}
                                >
                                    Автомобили
                                </MenuItem>
                            </CustomFormikMenu>
                        </div>
                        <div className='form-btns'>
                            <ButtonComponent onClick={handleCloseModal}>
                                <Typography>Отмена</Typography>
                            </ButtonComponent>
                            <ButtonComponent type='submit'>
                                <Typography>Продолжить</Typography>
                            </ButtonComponent>
                        </div>
                    </Form>
                )
            }}
        </Formik>
    )
};
