export const settings = {
    arrows: false,
    dots: false,
    slidesToShow: 3,
    responsive: [
        {
            breakpoint: 650,
            settings: {
                slidesToShow: 2,
            }
        },
        {
            breakpoint: 480,
            settings: {
                slidesToShow: 1,
            }
        }
    ]
};
