import {makeStyles} from '@material-ui/core/styles'


export const useStyles = makeStyles(() => ({
    root: {
        '& div.location': {
            display: 'flex',
            fontSize: '14px'
        },
        '& div.social-icons > a > img': {
            width: '35px',
            marginRight: '10px'
        },
        '& div.help-content': {
            display: 'flex',
            flexFlow: 'row wrap',
            '& a': {
                display: 'flex',
                textDecoration: 'none',
                alignItems: 'center',
                color: '#000',
            }
        },
        '& div > a img.help-content-icon': {
            textDecoration: 'none',
            height: '15px',
            marginRight: '10px',
        },
        '& img.pl-icon': {
            width: '32px',
            height: '23px',
        },
        '& div.localization ': {
            display: 'flex',
        },
        '& h6.localization-item:first-child': {
            marginRight: '8px',
        },
        '& h6.select-region': {
            textDecoration: 'underline',
        },
    }
}))
