import React, {FC} from "react";
import {Grid, Typography} from "@material-ui/core";
import {CustomMenu} from "@src/components/elements/custom_menu/CustomMenu";
import {useStyles} from './useStyles';


export const AdvrtSettingsBlock: FC<any> = (props) => {
    const {data, values, onBlur, handleClickMenuItem} = props;
    const keys = Object.keys(data);

    const classes = useStyles();
    return (
        <>
            <Grid item container xs={12} spacing={1} className={classes.root}>
                <Grid item xs={12}>
                    <Typography variant="h5">
                        <strong>Настройки объявления</strong>
                    </Typography>
                </Grid>
                <>
                    {
                        keys.map((key) => {
                            if (Array.isArray(data[key])) {
                                const newData = [{id: null, name: 'Выбрать'}, ...data[key]];
                                return listGenerator(newData, key);
                            }
                        })
                    }
                </>
            </Grid>
        </>
    )

    function listGenerator(data, key) {
        return (
            <Grid
                item
                key={key}
                xs={12}
                sm={4}
            >
                <Typography variant="subtitle1">
                    <strong>{key}</strong>
                </Typography>
                <CustomMenu
                    valueName={values[key] ? values[key].name : 'Выбрать'}
                    items={data}
                    onBlur={onBlur}
                    onClick={handleClickMenuItem(key)}
                />
                {
                    values[key] && Object.keys(values[key]).map(innerKey => {
                        if (Array.isArray(values[key][innerKey])) {
                            const newData = [{id: null, name: 'Выбрать'}, ...values[key][innerKey]];
                            return (
                                <>
                                    <Typography variant="subtitle1">
                                        <strong>{innerKey}</strong>
                                    </Typography>
                                    <CustomMenu
                                        valueName={'Выбрать'}
                                        items={newData}
                                        onBlur={onBlur}
                                        onClick={handleClickMenuItem(innerKey)}
                                    />
                                </>
                            );
                        }
                    })
                }
            </Grid>
        )
    }
};
