import {makeStyles} from '@material-ui/core/styles';

export const useStyles = makeStyles((theme) => ({
    root: {
        '& > h4': {
            marginBottom: 30,
        },
        '& div.tabs': {
            display: 'flex',
            width: '100%',
            minHeight: 'auto',
            '& div.MuiTabs-flexContainer': {
                borderBottom: '1px solid #838383',
                '& h6': {
                    textTransform: 'uppercase',
                    color: '#838383',
                },
                '& button.MuiTab-textColorInherit': {
                    opacity: 1,
                },
            },
        },
        '& div.cards-container': {
            marginTop: '40px',
        },
        
        '& div.ancmnts-wrapper, div.auction-wrapper': {
            '& > div.MuiGrid-container': {
                width: 'calc(100% + 14px)',
                margin: '-7px',
                '& div.MuiGrid-item': {
                    padding: '7px',
                },
            },
            '& a': {
                textDecoration: 'none',
            },
        },
        '& div.ads-wrapper': {
            '& div.card-header > div > h6.MuiTypography-subtitle2': {
                backgroundColor: 'rgba(136, 202, 236, .65)',
            },
        },
        '& div.lots-wrapper': {
            '& div.card-header > div > h6.MuiTypography-subtitle2': {
                backgroundColor: 'rgba(173, 102, 213, .65)',
            }
        },
        '& div.pagination': {
            display: 'flex',
            justifyContent: 'center',
            marginTop: '20px'
        }
    },
    showMoreContainer: {
        marginTop: '40px',
        [theme.breakpoints.down('md')]: {
            marginTop: '30px',
        },
        '& div.show-more-block': {
            position: 'relative',
            display: 'flex',
            justifyContent: 'center',
            '& > button': {
                position: 'relative',
                width: '170px',
                color: '#000',
                backgroundColor: theme.palette.primary.white,
                boxShadow: '0px 0px 8px rgba(132, 92, 171, 0.2)',
                borderRadius: '100px',
                zIndex: 1,
                '& > h6.MuiTypography-subtitle2': {
                    lineHeight: '17px',
                    letterSpacing: '0.25px',
                },
            },
        },
    }
}));
