import {createAsyncThunk, createSlice} from '@reduxjs/toolkit';
import {userAPI} from "@src/api/api";
import {CreateAdState} from '@root/interfaces/Advertisement';
import {CategoryIDs} from "@root/interfaces/Categories";


const initialState: CreateAdState = {
    isFetch: false,
    error: null,
    data:{}
};

export const fetchAd = createAsyncThunk<any, CategoryIDs>(
    'createAdvrt/fetchAd',
    async ({ctgryID, subCtgryID, lang}, {rejectWithValue}) => {
        try {
            return await userAPI.getAdDataForCreate(ctgryID, subCtgryID, lang);
        } catch (e) {
            return rejectWithValue(e.message);
        }
    }
);

const createAdvrtSlice = createSlice({
    name: 'createAdvrt',
    initialState,
    reducers: {
        setAdvrtCtgryAction: (state, action) => {
            state.adType = action.payload.adType;
            state.adCategory = action.payload.adCategory;
        }
    },
    extraReducers: (builder) => {
        builder.addCase(fetchAd.pending, (state) => {
            state.isFetch = true;
            state.error = null;
        })
        builder.addCase(fetchAd.fulfilled, (state, action) => {
            state.isFetch = false;
            state.adType = action.payload;
        })
        builder.addCase(fetchAd.rejected, (state, action) => {
            state.isFetch = false;
            state.error = action.payload;
        })
    }
});

export const {setAdvrtCtgryAction} = createAdvrtSlice.actions;
export const createAdvrtReducer = createAdvrtSlice.reducer;
