import React from "react"
import {TextField} from "@material-ui/core"
import {Field} from "formik";
import {phoneValidate} from "../../validates";


export const CustomField = ({setFieldValue, ...props}) => {

    const handler = ({target}) => {
        const {value} = target;

        setFieldValue(props.name, value);
    };

    return (
        <Field validate={phoneValidate} name='phone'>
            {({field}) => {
                return (
                    <TextField
                        fullWidth
                        focused={false}
                        variant='outlined'
                        onChange={handler}
                        {...props}
                        {...field}
                    />
                )
            }}
        </Field>
    )

    return
};
