import {makeStyles} from '@material-ui/core/styles'

export const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        '& div.tabs div.MuiTabs-flexContainer': {
            justifyContent: 'space-around'
        },
        '& div.cards-container': {
            marginTop: '10px'
        },
        '& div.ads-wrapper': {
            '& div.card-title > h6': {
                backgroundColor: theme.palette.primary.adBgColor
            }
        },
        '& div.lots-wrapper': {
            '& div.card-title > h6': {
                backgroundColor: theme.palette.primary.lotBgColor
            }
        }
    }
}))
