import {makeStyles} from '@material-ui/core/styles'

export const useStyles = makeStyles((theme) => ({
    root: {
        '& div.tabs div.MuiTabs-flexContainer': {
            justifyContent: 'space-around'
        },
        '& div.cards-container': {
            marginTop: '10px'
        },
        '& div.ads-wrapper': {
            '& div.card-title > h6': {
                backgroundColor: theme.palette.primary.adBgColor
            }
        },
        '& div.lots-wrapper': {
            '& div.card-title > h6': {
                backgroundColor: theme.palette.primary.lotBgColor
            }
        }
    },
    adBanner: {
        display: 'flex',
        flexDirection: 'column',
        '& > div': {
            backgroundColor: 'gray'
        },
        '& > div.top-banner': {
            height: '250px'
        },
        '& > div.central-banner': {
            height: '130px'
        },
        '& > div.bottom-banner': {
            height: '250px'
        }
    }
}))
