import {makeStyles} from '@material-ui/core/styles'

export const useStyles = makeStyles(({palette}) => ({
    root: {
        padding: '10px',
        backgroundColor: palette.primary.black,
        '& svg': {
            width: '12px',
            height: '12px'
        },
        '&:hover svg > path': {
            fill: palette.primary.error
        }
    }
}));
