import React, {FC} from "react";
import {Tabs, Typography, InputBase, List, ListItem, Grid, Tab} from "@material-ui/core";
import {useStyles} from './useStyles';
import {CustomTabPanel} from "@src/components/elements/custom_tab_panel/CustomTabPanel";


export const CreateAdvrt: FC<any> = (props) => {
    const {
        adTypes,
        tabValue,
        handleTab,
        handleCategory,
        handleSubCategory,
        createAdvrt,
        categoriesList,
    } = props;

    const {adType, category} = createAdvrt;

    const classes = useStyles();
    return (
        <div className={classes.root}>
            <Tabs
                value={tabValue}
                onChange={handleTab}
                centered
            >
                {
                    adTypes.map(type => (
                        <Tab
                            key={type.id}
                            value={type.id}
                            label={
                                <Typography variant="subtitle1">
                                    {type.name}
                                </Typography>
                            }
                        />
                    ))
                }
            </Tabs>
            <Grid container className={classes.tabsContent}>
                <Grid item xs={3} className='categories-block'>
                    <div className='header'>
                        <Typography>Выберите категорию</Typography>
                    </div>
                    <List className='categories-list'>
                        {
                            categoriesList.map((ctgr) => (
                                (adType.id === 1 || ctgr.has_auction === 1) && (
                                    <ListItem key={ctgr.id} onClick={handleCategory(ctgr.id, ctgr.name, ctgr.childs)}>
                                        <div>
                                            <img src={ctgr.icons.url} alt='category'/>
                                            {ctgr.name}
                                        </div>
                                    </ListItem>
                                )
                            ))
                        }
                    </List>
                </Grid>
                <Grid item xs={9} className='sub-categories-block'>
                    <div className='search-block'>
                        <InputBase style={{border: '1px solid'}} placeholder='Поиск по категориям'/>
                    </div>
                    <List className='categories-list'>
                        {
                            adTypes.map(type => (
                                <CustomTabPanel key={type.id} value={tabValue} index={type.id}>
                                    {
                                        category.childs.length
                                            ? category.childs.map(({id, name}) => (
                                                <ListItem key={id} onClick={handleSubCategory(id, name)}>
                                                    <div>
                                                        {name}
                                                    </div>
                                                </ListItem>
                                            ))
                                            : <ListItem onClick={handleSubCategory()}>
                                                <div>
                                                    {category.name}
                                                </div>
                                            </ListItem>
                                    }
                                </CustomTabPanel>
                            ))
                        }
                    </List>
                </Grid>
            </Grid>
        </div>
    )
};
