import {userApi} from '../../api/api'
import {authErrorAction} from './errorsActions'

export const selfDataActionTypes = {
    FETCH_SELF_DATA_BEGIN: 'FETCH_SELF_DATA_BEGIN',
    FETCH_SELF_DATA_SUCCESS: 'FETCH_SELF_DATA_SUCCESS',
    FETCH_SELF_DATA_FAILURE: 'FETCH_SELF_DATA_FAILURE',
};

const selfDataActions = {
    fetchSelfDataBegin: () => ({
        type: selfDataActionTypes.FETCH_SELF_DATA_BEGIN
    }),
    fetchSelfDataSuccess: (selfData) => ({
        type: selfDataActionTypes.FETCH_SELF_DATA_SUCCESS,
        payload: selfData
    })
};

export const getSelfData = async (phone, password) => {
    try {
        const {userInfo} = await userApi.login(phone, password);
        return {
            type: selfDataActionTypes.FETCH_SELF_DATA_BEGIN,
            payload: {
                isFetch: false,
                selfData: userInfo
            }
        }
    } catch (e) {
        authErrorAction(e)
    }
};
