import {makeStyles} from '@material-ui/core/styles'

export const useStyles = makeStyles((theme) => ({
    root: {
        backgroundColor: theme.palette.primary.white,
    },
    tabsContent: {
        '& ul.categories-list': {
            // height: '498px',
            // overflowY: 'scroll',
        }
    },
    adBanner: {
        marginTop: '140px',
        '& > div.right-banner': {
            width: '100%',
            height: '470px',
            borderRadius: '7px',
            backgroundColor: '#C0C0C0',
        },
    }
}))
