import {makeStyles} from '@material-ui/core/styles'

export const useStyles = makeStyles(() => ({
    root: {
        position: 'relative',
        maxWidth: '1920px',
        padding: '0 19px',
        margin: '0 auto',
        '& div.slick-slider': {
            zIndex: 10,
            '& img': {
                width: '100%'
            },
            '& button span svg:hover': {
                fill: '#a153ff',
            },
            '& div.slider-arrows-container': {
                position: 'absolute',
                top: 0,
                display: 'flex',
                justifyContent: 'space-between',
                width: '100%',
                height: '100%'
            }
        }
    }
}));
