import {makeStyles} from '@material-ui/core/styles'

export const useStyles = makeStyles(({palette}) => ({
    root: {
        width: '725px',
        height: '540px',
        borderRadius: '6px',
        backgroundColor: 'white',
        '& div.info-block': {
            padding: '15px',
            backgroundImage: 'linear-gradient(rgba(0,0,0,.6), rgba(0,0,0,.6)), url("./img/modal-image.jpg")',
            backgroundSize: 'cover',
            borderTopLeftRadius: '6px',
            borderBottomLeftRadius: '6px',
            '& h6.MuiTypography-root': {
                lineHeight: '14px',
                color: '#fff'
            },
            '& > div, & > div > div': {
                display: 'flex',
            },
            '& > div': {
                flexDirection: 'column',
                justifyContent: 'space-around',
                height: '100%',
                '& > div': {
                    alignItems: 'center',
                    '& > img': {
                        marginRight: '15px'
                    }
                }
            }
        },
        '& div.auth-reg-block': {
            height: '540px',
            padding: '20px 15px',
            '& > div.welcome-block': {
                '& > h6': {
                    textAlign: 'center'
                }
            },
            '& div.tabs-container': {
                marginTop: '30px',
                '& div.tabs': {
                    width: '100%',
                    '& button.MuiButtonBase-root': {
                        width: '50%',
                        borderBottom: '1px solid rgba(0,0,0,.23)'
                    }
                },
                '& div.tab-panels': {
                    marginTop: '20px',
                    '& > div.sign-panel, & > div.reg-panel': {
                        '& > form': {
                            display: 'flex',
                            flexDirection: 'column',
                        }
                    },
                    '& > div.sign-panel': {
                        justifyContent: 'space-between',
                        height: '300px',
                        '& a': {
                            textDecoration: 'none',
                            '& > p': {
                                margin: '10px 0',
                                textAlign: 'end',
                                color: palette.primary.main,
                            }
                        }
                    },
                    '& > div.reg-panel': {
                        justifyContent: 'space-around',
                        height: '290px',
                    }
                }
            }
        },
        '& div.forget-password': {
            display: 'flex',
            justifyContent: 'flex-end',
        }
    },
    modalBtns: {
        display: 'flex',
        justifyContent: 'center',
        '& button.signin-btn, & button.reg-btn': {
            color: '#fff',
            background: "linear-gradient(49.94deg, #675EAA 19.03%, #AD66D5 72.72%)",
            borderRadius: '3px',
            padding: '15px 0',
            width: '50%'
        },
        '& button.reg-btn': {
            marginTop: '40px'
        }
    },
    agreement: {
        textAlign: 'center',
        '& > p > a': {
            color: palette.primary.main,
            textDecoration: 'none'
        }
    },
    // Errors
    errorInput: {
        '& fieldset': {
            borderColor: `${palette.primary.error}!important`
        }
    },
    errorTxt: {
        height: '15px',
        margin: '5px',
        color: palette.primary.error
    }
}))
