import React, {FC, useState} from 'react';
import {
    Grid,
    Typography,
    TextField,
    Checkbox,
    InputAdornment,
    TextareaAutosize,
    Hidden,
} from '@material-ui/core';
import {ButtonComponent} from '@src/components/elements/button/Button';
import {useSelector} from "react-redux";
import {RootState} from "@src/redux/rootReducer";
//icons
import {Help, Search} from '@material-ui/icons';
import {Formik, FormikProps} from "formik";
import {adTypeAndCtgrySchema} from "@src/validation_schemas/validationSchemas";
import {CustomMenu} from "@src/components/elements/custom_menu/CustomMenu";
import {adTypesList} from "@src/components/advertisement/createAdModalForm/CreateAdModalForm";
// styles
import {useStyles} from './useStyles';


export const CreateAdvertisement: FC<any> = (props) => {

    const store = useSelector((store: RootState) => store);
    const {categories, createAdvrt} = store;

    const [typeAnchor, setTypeAnchor] = useState(null);
    const [categoryAnchor, setCategoryAnchor] = useState(null);
    const [subCategoryAnchor, setSubCategoryAnchor] = useState(null);


    const handleMenuOpen = (setAnchor) => (e) => {
        setAnchor(e.currentTarget);
    };

    const handleMenuClose = (setAnchor) => () => {
        setAnchor(null);
    };

    const initVals = {
        adType: {
            id: null,
            name: 'Тип объявления'
        },
        adCategory: {
            id: null,
            name: 'Выберите категорию',
            childs: []
        },
        adSubCategory: {
            id: null,
            name: 'Под категория'
        }
    };

    const submit = () => {
        console.log('submit')
    };

    const classes = useStyles();
    return (
        <div className={classes.root}>
            <Formik initialValues={initVals} validationSchema={adTypeAndCtgrySchema} onSubmit={submit}>
                {
                    ({
                         errors,
                         touched,
                         values,
                         setValues,
                         handleBlur
                     }: FormikProps<any> & { errors: any }) => {

                        const handleChange = (event) => {
                            setValues(event.target.value)
                        };

                        const handleCheckboxChange = (event) => {
                            setValues(event.target.checked)
                        };

                        const handleClickMenuItem = (valueName, setAnchor) => (newValue) => () => {
                            setValues({...values, [valueName]: newValue});
                            handleMenuClose(setAnchor)();
                        };

                        const handleClickMenuCategory = (setAnchor) => (newValue) => () => {
                            setValues({
                                ...values,
                                adCategory: newValue,
                                adSubCategory: newValue.childs[0]
                            });
                            handleMenuClose(setAnchor)();
                        };

                        console.log(values)
                        return (
                            <>
                                <Typography variant="h4" className={classes.title}>
                                    Новое объявление - <span>{values.adCategory.name}</span>
                                </Typography>
                                <Grid container justify="space-between" spacing={1}>
                                    <Grid
                                        container
                                        item
                                        xs={12}
                                        justify="space-between"
                                        spacing={2}
                                    >
                                        <Grid
                                            item
                                            xs={12}
                                            sm={4}
                                            container
                                            justify="space-between"
                                        >
                                            <Typography variant="subtitle1">
                                                <strong>
                                                    Тип
                                                    <span className={classes.required}>*</span>
                                                </strong>
                                            </Typography>
                                            <ButtonComponent
                                                style={{
                                                    backgroundColor: '#fafafa',
                                                    border: '1px solid #4e4e4e',
                                                    color: '#4e4e4e',
                                                }}
                                                onClick={handleMenuOpen(setTypeAnchor)}
                                            >
                                                {values.adType.name}
                                            </ButtonComponent>
                                            <CustomMenu
                                                items={adTypesList}
                                                open={Boolean(typeAnchor)}
                                                anchorEl={typeAnchor}
                                                onBlur={handleBlur}
                                                onClose={handleMenuClose(setTypeAnchor)}
                                                onClick={handleClickMenuItem('adType', setTypeAnchor)}
                                            />
                                        </Grid>
                                        <Grid
                                            item
                                            xs={12}
                                            sm={4}
                                            container
                                            alignItems="center"
                                        >
                                            <Typography variant="subtitle1">
                                                <strong>
                                                    Категория
                                                    <span className={classes.required}>
                                                    *
                                                    </span>
                                                </strong>
                                            </Typography>
                                            <ButtonComponent
                                                style={{
                                                    backgroundColor: '#fafafa',
                                                    border: '1px solid #4e4e4e',
                                                    color: '#4e4e4e',
                                                }}
                                                onClick={handleMenuOpen(setCategoryAnchor)}
                                            >
                                                {values.adCategory.name}
                                            </ButtonComponent>
                                            <CustomMenu
                                                items={categories.list}
                                                open={Boolean(categoryAnchor)}
                                                anchorEl={categoryAnchor}
                                                onBlur={handleBlur}
                                                onClose={handleMenuClose(setCategoryAnchor)}
                                                onClick={handleClickMenuCategory(setCategoryAnchor)}
                                            />
                                        </Grid>
                                        <Grid
                                            item
                                            xs={12}
                                            sm={4}
                                            container
                                            alignItems="center"
                                        >
                                            <ButtonComponent
                                                style={{
                                                    backgroundColor: '#fafafa',
                                                    border: '1px solid #4e4e4e',
                                                    color: '#4e4e4e',
                                                }}
                                                onClick={handleMenuOpen(setSubCategoryAnchor)}
                                                disabled={!values.adCategory.childs.length}
                                            >
                                                {values.adSubCategory.name}
                                            </ButtonComponent>
                                            <CustomMenu
                                                items={values.adCategory.childs}
                                                open={Boolean(subCategoryAnchor)}
                                                anchorEl={subCategoryAnchor}
                                                onBlur={handleBlur}
                                                onClose={handleMenuClose(setSubCategoryAnchor)}
                                                onClick={handleClickMenuItem('adSubCategory', setSubCategoryAnchor)}
                                            />
                                        </Grid>
                                    </Grid>
                                    <div className={classes.advertisementSettings}>
                                        <Typography variant="h5">
                                            <strong>Настройки объявления</strong>
                                        </Typography>
                                    </div>
                                    <Grid
                                        container
                                        item
                                        xs={12}
                                        direction="row"
                                        alignItems="center"
                                        justify="space-between"
                                        spacing={2}
                                    >
                                        <Grid item xs={12} sm={6} md={3} lg={3} xl={3}>
                                            <Typography variant="subtitle1">
                                                <strong>
                                                    Название объявления
                                                    <span className={classes.required}>
                                                    *
                                                    </span>
                                                </strong>
                                            </Typography>
                                        </Grid>
                                        <Grid item xs={12} sm={6} md={9} lg={9} xl={9}>
                                            <TextField
                                                fullWidth
                                                id="advertisementName"
                                                variant="outlined"
                                                helperText="Название не должно превышать 50 символов"
                                                placeholder="Пример: Samsung S9 black 64 Gb"
                                            />
                                        </Grid>
                                    </Grid>
                                    <Grid item xs={12}>
                                        <Typography variant="h5" color="initial">
                                            <strong>Оплата и доставка</strong>
                                        </Typography>
                                    </Grid>
                                    <Grid
                                        container
                                        item
                                        xs={12}
                                        direction="row"
                                        alignItems="center"
                                        justify="space-between"
                                    >
                                        <Grid item xs={3}>
                                            <Typography variant="subtitle1">
                                                Безопасный торг
                                            </Typography>
                                        </Grid>
                                        <Grid
                                            item
                                            xs={9}
                                            className={classes.paymentAndDelivery}
                                        >
                                            <Checkbox
                                                checked={true}
                                                onChange={handleCheckboxChange}
                                                color="primary"
                                            />
                                            <Help className="question-mark"/>
                                            <Typography variant="subtitle2">
                                                Примечание: Вы подключили услугу «Безопасный
                                                торг». Ваша сделка защищена. Стоимость
                                                услуги составляет n%.{' '}
                                                <a href="#">
                                    <span className="safe-auction-rules">
                                        Ознакомиться с правилами «Безопасный
                                        торг»
                                    </span>
                                                </a>
                                            </Typography>
                                        </Grid>
                                    </Grid>
                                    <Grid
                                        container
                                        item
                                        xs={12}
                                        direction="row"
                                        alignItems="center"
                                        justify="space-between"
                                    >
                                        <Grid item xs={3}>
                                            <Typography variant="subtitle1">
                                                Есть доставка
                                            </Typography>
                                        </Grid>

                                        <Grid
                                            item
                                            xs={9}
                                            className={classes.paymentAndDelivery}
                                        >
                                            <Checkbox
                                                checked={true}
                                                onChange={handleCheckboxChange}
                                                color="primary"
                                            />
                                            <Help className="question-mark"/>
                                            <Typography variant="subtitle2">
                                                Примечание: Вы будете выделены специальным
                                                стикером доставка. Доставка осуществляется
                                                за Ваш счет. В случае невыполнения доставки,
                                                Вы можете быть заблокированы.
                                                <a href="#">
                                    <span className="safe-auction-rules">
                                        Ознакомиться с правилами «Есть
                                        доставка»
                                    </span>
                                                </a>
                                            </Typography>
                                        </Grid>
                                    </Grid>
                                    <Grid
                                        container
                                        item
                                        xs={12}
                                        direction="row"
                                        alignItems="center"
                                        justify="space-between"
                                    >
                                        <Grid item xs={3}>
                                            <Typography variant="subtitle1">
                                                Возможен обмен
                                            </Typography>
                                        </Grid>

                                        <Grid
                                            item
                                            xs={9}
                                            className={classes.paymentAndDelivery}
                                        >
                                            <Checkbox
                                                checked={true}
                                                onChange={handleCheckboxChange}
                                                color="primary"
                                                inputProps={{
                                                    'aria-label': 'secondary checkbox',
                                                }}
                                            />
                                            <Help className="question-mark"/>
                                            <Typography variant="subtitle2">
                                                Примечание: Вы принимаете предложения от
                                                других пользователей на обмен. Вы будете
                                                выделены специальным <br/>
                                                стикером «Возможен обмен»
                                            </Typography>
                                        </Grid>
                                    </Grid>
                                    <Grid
                                        container
                                        item
                                        xs={12}
                                        direction="row"
                                        justify="space-between"
                                        spacing={2}
                                        className={classes.advertisementName}
                                    >
                                        <Grid item xs={12} sm={3} md={3} lg={3} xl={3}>
                                            <Typography variant="subtitle1">
                                                Местоположение
                                            </Typography>
                                        </Grid>

                                        <Grid item xs={12} sm={9}>
                                            <TextField
                                                fullWidth
                                                variant="outlined"
                                                InputProps={{
                                                    startAdornment: (
                                                        <InputAdornment position="start">
                                                            <Search/>
                                                        </InputAdornment>
                                                    ),
                                                }}
                                            />
                                        </Grid>
                                        <Hidden xsDown>
                                            <Grid item sm={3}/>
                                        </Hidden>
                                        <Grid item xs={12} sm={9}>
                                            <div className="photos-dropdown"/>
                                        </Grid>
                                    </Grid>
                                    <Grid
                                        container
                                        item
                                        xs={12}
                                        spacing={2}
                                        direction="row"
                                        alignItems="center"
                                        justify="space-between"
                                    >
                                        <Grid item xs={12} sm={3}>
                                            <Typography variant="subtitle1">
                                                <strong>
                                                    Фотографии
                                                    <span className={classes.required}>
                                                    *
                                                </span>
                                                </strong>
                                            </Typography>
                                        </Grid>
                                    </Grid>
                                    <Grid
                                        container
                                        item
                                        xs={12}
                                        direction="row"
                                        alignItems="center"
                                        justify="space-between"
                                        spacing={2}
                                    >
                                        <Grid item xs={12} sm={3} md={3}>
                                            <Typography variant="subtitle1">
                                                <strong>
                                                    Описание{' '}
                                                    <span className={classes.required}>
                                        *
                                    </span>
                                                </strong>
                                            </Typography>
                                        </Grid>
                                        <Grid item xs={12} sm={9}>
                                            <TextareaAutosize
                                                className={classes.description}
                                                aria-label="Phone"
                                                rowsMin={15}
                                            />
                                        </Grid>
                                    </Grid>
                                    <Grid
                                        container
                                        item
                                        xs={12}
                                        direction="row"
                                        alignItems="center"
                                        spacing={2}
                                    >
                                        <Grid item xs={12} sm={3} md={3}>
                                            <Typography variant="subtitle1">
                                                <strong>
                                                    Телефон{' '}
                                                    <span className={classes.required}>
                                        *
                                    </span>
                                                </strong>
                                            </Typography>
                                        </Grid>
                                        <Grid item xs={12} sm={4} md={3}>
                                            <TextField
                                                variant="outlined"
                                                id="phone"
                                                placeholder="+998 (__) ___ __ __"
                                                type="tel"
                                                fullWidth
                                            />
                                        </Grid>
                                        <Grid
                                            item
                                            xs={12}
                                            sm={5}
                                            md={6}
                                            container
                                            alignItems="center"
                                            justify="space-around"
                                        >
                                            <Typography variant="subtitle2">
                                                Время звонков
                                            </Typography>
                                            <a href="#" className={classes.getSettings}>
                                                Перейти к настройкам
                                            </a>
                                        </Grid>
                                    </Grid>
                                    <div className={classes.nextButtonBlock}>
                                        <ButtonComponent
                                            type='submit'
                                            className={classes.nextButton}
                                            onClick={props.handlePreview}
                                        >
                                            Далее
                                        </ButtonComponent>
                                    </div>
                                </Grid>
                            </>
                        )
                    }
                }
            </Formik>
        </div>
    )
}
