import {string, object, array} from "yup";


export const createAdvrtSchema = object({
    title: string().required('Обязательное поле!'),
    location: string().required('Обязательное поле!'),
    files: array().required('Загрузите фотографии!'),
    description: string().required('Обязательное поле!'),
    phone: string().required('Обязательное поле!'),
    adsParams: object().shape({
        
    })
});
