import React, {FC} from "react";
import {Grid, Typography} from "@material-ui/core";
import {CustomMenu} from "@src/components/elements/custom_menu/CustomMenu";
import {useStyles} from './useStyles';


export const AdvrtSettingsBlock: FC<any> = (props) => {
    const {data, values, onBlur, handleClickMenuItem} = props;
    const dataKeys = Object.keys(data);

    const classes = useStyles();
    return (
        <>
            <Grid item container xs={12} spacing={1} className={classes.root}>
                <Grid item xs={12}>
                    <Typography variant="h5">
                        <strong>Настройки объявления</strong>
                    </Typography>
                </Grid>
                {
                    dataKeys.map((key) => {
                        if (Array.isArray(values[key])) {
                            const newData = key !== 'adsParams' ? [{id: 0, name: 'Выбрать'}, ...data[key]] : data[key];
                            // console.log(values['type'])
                            return (
                                <>
                                    <Grid
                                        key={key}
                                        item
                                        xs={12}
                                        sm={4}
                                    >
                                        <Typography variant="subtitle1">
                                            <strong>{key}</strong>
                                        </Typography>
                                        <CustomMenu
                                            valueName={values[key] ? values[key].name : 'Выбрать'}
                                            items={newData}
                                            onBlur={onBlur}
                                            onClick={handleClickMenuItem(key)}
                                        />
                                    </Grid>
                                    {
                                        values['type'] && values['type'].sub_type && (
                                            <Grid
                                                key={key}
                                                item
                                                xs={12}
                                                sm={4}
                                            >
                                                <Typography variant="subtitle1">
                                                    <strong>{'sub_type'}</strong>
                                                </Typography>
                                                <CustomMenu
                                                    valueName={values['sub_type'] ? values['sub_type'].name : 'Выбрать'}
                                                    items={values['type'].sub_type}
                                                    onBlur={onBlur}
                                                    onClick={handleClickMenuItem('sub_type')}
                                                />
                                            </Grid>
                                        )
                                    }
                                </>
                            )
                        }
                    })
                }
            </Grid>
        </>
    )
};
