import React from "react";
import {useScrollTrigger} from "@material-ui/core";


export const withScrollThreshold = (Component) => props => {
    const isFirstBreak = useScrollTrigger({disableHysteresis: true, threshold: 40});
    const isSecondBreak = useScrollTrigger({disableHysteresis: true, threshold: 45});
    return (
        <Component {...props} isSecondBreak={isSecondBreak} isFirstBreak={isFirstBreak}/>
    );
};
