import {userApi} from '../api/api'
import {call, put, takeLatest} from "redux-saga/effects";
import {selfDataActionTypes, selfDataActions} from "./actions/selfDataActions";

const {FETCH_SELF_DATA_BEGIN} = selfDataActionTypes;
const {fetchSelfDataBegin, fetchSelfDataSuccess, fetchSelfDataFailure} = selfDataActions;

function* workerFetchSelfData(action) {
    try {
        yield put(fetchSelfDataBegin(action.phone, action.password));
        const data = yield call(userApi.login,);
        yield put(fetchSelfDataSuccess(data));
    } catch (e) {
        yield put(fetchSelfDataFailure(e));
    }
}

export default function* watchFetchSelfData() {
    yield all([
        takeLatest(FETCH_SELF_DATA_BEGIN, workerFetchSelfData)
    ])
}

export default function* watchAll() {
    yield all([
        watchFetchSelfData()
    ])
}
