import {Typography} from '@material-ui/core';
import React, {Dispatch, FC} from 'react';
import {DragDropContext, Droppable, Draggable, resetServerContext} from 'react-beautiful-dnd';
import {useStyles} from './useStyles';

resetServerContext();

export const PreviewPhotos: FC<{ images: [], setImages: Dispatch<any> }> = (props) => {
    const {images, setImages} = props;

    const handleOnDragEnd = (result) => {
        if (!result.destination) return;

        const items = Array.from(images);
        const [reorderedItem] = items.splice(result.source.index, 1);
        items.splice(result.destination.index, 0, reorderedItem);

        setImages(items);
    };

    const getBase64 = (file) => {
        const reader = new FileReader();
        return new Promise((res, rej) => {
            reader.readAsDataURL(file);
            reader.onload = function () {
                res(reader.result)
            };
            reader.onerror = (error) => {
                rej(error);
            };
        })
    };

    const handleUploadFile = (e) => {
        if (e.target.files && e.target.files[0]) {
            setImages([{
                file: e.target.files[0],
                img: URL.createObjectURL(e.target.files[0])
            },
                ...images
            ]);
        }
    };

    const classes = useStyles();
    return (
        <div className={classes.root}>
            <input type='file' onChange={handleUploadFile}/>
            <DragDropContext onDragEnd={handleOnDragEnd}>
                <Droppable droppableId="images" direction="horizontal">
                    {(provided) => (
                        <div className="images-wrapper" {...provided.droppableProps} ref={provided.innerRef}>
                            {images.map(({file, img}, index) => {
                                return (
                                    <Draggable key={file.name} draggableId={file.name} index={index}>
                                        {(provided) => (
                                            <div
                                                ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                                                <img src={img} style={{width: '120px', height: '90px'}} alt={file.name}/>
                                                <Typography>{file.name}</Typography>
                                            </div>
                                        )}
                                    </Draggable>
                                );
                            })}
                            {provided.placeholder}
                        </div>
                    )}
                </Droppable>
            </DragDropContext>
        </div>
    )
};
