import React, {FC} from "react";
import {Autocomplete} from "@material-ui/lab";
import {CustomFormikField} from "@src/components/elements/custom_formik_field/CustomFormikField";


const option = ({area, city, district}) => {
    return `${district ? `${district}, ` : ''}${city}, ${area}`;
};

const formatData = (list) => {
    return list.reduce((acc, row) => {
        row.cities.forEach((city) => {
            if (city.district.length) {
                city.district.forEach((dis) => {
                    acc = [
                        ...acc,
                        {
                            area: row.name,
                            city: city.name,
                            district: dis.name
                        }
                    ];
                });
            } else {
                acc = [
                    ...acc,
                    {
                        area: row.name,
                        city: city.name
                    }
                ];
            }
        });
        return acc;
    }, []);
}

export const AddressAutocomplete: FC<{ placeholder: string, name: string, value: string, list: any[] }> = (props) => {
    return (
        <Autocomplete
            options={formatData(props.list)}
            getOptionLabel={option}
            renderInput={(params) => {
                console.log(params)
                return <CustomFormikField {...params} {...props} />
            }}
        />
    )
};
