import * as yup from "yup";


export const requiredFields = {
    mileage: true,
    body: true,
    year: true,
    car_engine_type: true,
};

const requiredMsg = 'Обязательное поле!';

const lazyFunc = (value: string) => {
    const keys = Object.keys(requiredFields);
    const fields = yup.object();

    keys.forEach(k => {
        if (value[k]) {
            fields.shape({
                [k]: yup.object({
                    id: yup.number().nullable().required(requiredMsg)
                })
            })
        }
    });
    console.log(value)

    if (Object.keys(fields.fields).length) {
        return fields;
    }

    return yup.mixed().notRequired();
};

export const createAdvrtSchema = yup.object({
