import {makeStyles} from '@material-ui/core/styles'


export const useStyles = makeStyles((theme) => ({
    root: {
        width: '800px',
        height: '570px',
        backgroundColor: theme.palette.primary.white,
    },
    tabsContent: {
        '& > div.header, & > div.body': {
            display: 'flex'
        },
        '& ul.categories-list': {
            height: '500px',
            overflow: 'scroll',
        }
    }
}))
