import React, {useState} from "react"
import {Formik, Form} from "formik"
import {useRouter} from "next/router"
import {MenuItem, Typography} from "@material-ui/core"
import {ButtonComponent} from "@src/components/elements/button/Button"
import {object, number} from 'yup'
import {useStyles} from './useStyles'
import {CustomFormikMenu} from "@src/components/elements/custom_formik_menu/CustomFormikMenu"

const initValues = {
    type: {
        id: '',
        desc: 'Выберите тип',
    },
    category: {
        id: '',
        desc: 'Выберите категорию'
    }
};

export const CreateAdModalForm = (props) => {
    const {handleCloseModal} = props;

    const router = useRouter();

    const [anchorEl, setAnchorEl] = useState(null);

    const validationSchema = object({
        type: object({
            id: number().required('Выберите тип объявления!')
        }),
        category: object({
            id: number().required('Выберите категорию!')
        })
    });

    const handleMenuOpen = (e) => {
        setAnchorEl(e.currentTarget)
    };

    const handleMenuClose = () => {
        setAnchorEl(null)
    };

    const handleClickMenuItem = (handleSetValue, values, newValue) => () => {
        handleSetValue({...values, ...newValue});
    };

    const submit = () => {
        router.push('/create_advertisement');
    };

    const classes = useStyles();
    return (
        <Formik initialValues={initValues} validationSchema={validationSchema} onSubmit={submit}>
            {({errors, touched, values, setValues}) => {
                console.log(errors)
                return (
                    <Form className={classes.root}>
                        <div>
                            {/*<Typography>{errors}</Typography>*/}
                            <ButtonComponent onClick={handleMenuOpen}>{values.type.desc}</ButtonComponent>
                            <CustomFormikMenu
                                name={values}
                                anchorEl={anchorEl}
                                open={Boolean(anchorEl)}
                                onClose={handleMenuClose}
                                setValues={setValues}
                            >
                                <MenuItem
                                    value={1}
                                    onClick={handleClickMenuItem(setValues, values, {
                                        type: {
                                            id: 1,
                                            desc: 'Объявление'
                                        }
                                    })}
                                >
                                    Объявление
                                </MenuItem>
                                <MenuItem
                                    value={2}
                                    onClick={handleClickMenuItem(setValues, values, {type: {id: 1, desc: 'Торг'}})}
                                >
                                    Торг
                                </MenuItem>
                            </CustomFormikMenu>
                        </div>
                        <div className='form-btns'>
                            <ButtonComponent onClick={handleCloseModal}>
                                <Typography>Отмена</Typography>
                            </ButtonComponent>
                            <ButtonComponent type='submit'>
                                <Typography>Продолжить</Typography>
                            </ButtonComponent>
                        </div>
                    </Form>
                )
            }}
        </Formik>
    )
};
