import React, {FC, useEffect, useState} from 'react';
import {Grid, Hidden, Container} from '@material-ui/core';
import {useSelector} from "react-redux";
import {RootState} from "@src/redux/rootReducer";
import {MainLayout} from "@src/components/MainLayout";
import {AdvrtFormContainer} from './advrt_form/AdvrtFormContainer';
import {useStyles} from './useStyles';
import {i18n} from "@root/i18n";
import {userAPI} from '@src/api/api';
import {CreateAdvrt} from "@src/components/advertisement/create_advrt/CreateAdvrt";


const initCreateAdState = {
    isFetch: false,
    error: null,
    adType: {
        id: null,
        name: ''
    },
    category: {
        id: null,
        name: '',
        childs: []
    },
    subCategory: {
        id: null,
        name: '',
        data: null
    }
};

export const CreateAdvrtContainer: FC<void> = () => {
    const lang = i18n.language;

    const categoriesList = useSelector(({categories}: RootState) => categories.list);

    const [tabValue, setTabValue] = useState(0);

    const [createAdvrt, setCreateAdvrt] = useState(initCreateAdState);

    const [adTypes, setAdTypes] = useState(null);

    const {category, subCategory} = createAdvrt;

    const getAdTypes = async () => {
        try {
            setCreateAdvrt({...createAdvrt, isFetch: true});
            const adTypes = await userAPI.getAdTypes(lang);
            setCreateAdvrt({...createAdvrt, isFetch: false});

            setAdTypes(adTypes);
        } catch (error) {
            setCreateAdvrt({...createAdvrt, error});
        }
    };

    const handleTab = (_, newValue) => {
        setTabValue(newValue);
        switch (tabValue) {
            case 0:
                setCreateAdvrt({...createAdvrt, adType: adTypes[0]});
                break;
            case 1:
                setCreateAdvrt({...createAdvrt, adType: adTypes[1]});
                break;

        }
    };

    const handleCtgr = (id, name, childs) => () => {
        setCreateAdvrt({
            ...createAdvrt,
            category: {
                id,
                name,
                childs
            }
        });
    };

    const handleSubCtgr = (subCtgryID) => async () => {
        const dataForCreateAd = await userAPI.getAdDataForCreate(category.id, subCtgryID, lang)
        setCreateAdvrt({
            ...createAdvrt,
            subCategory: {
                ...subCategory,
                data: dataForCreateAd
            }
        });
    };

    useEffect(() => {
        getAdTypes();
    }, [])

    const classes = useStyles();
    return (
        <MainLayout>
            <Container maxWidth="lg">
                <Grid container spacing={1}>
                    <Grid item xs={12} md={9}>
                        {
                            createAdvrt.category.id
                                ? <AdvrtFormContainer createAdvrt={createAdvrt}/>
                                : <CreateAdvrt
                                    handleTab={handleTab}
                                    handleCtgr={handleCtgr}
                                    handleSubCtgr={handleSubCtgr}
                                    tabValue={tabValue}
                                    createAdvrt={createAdvrt}
                                    categoriesList={categoriesList}
                                />
                        }
                    </Grid>
                    <Hidden smDown>
                        <Grid item md={3} className={classes.adBanner}>
                            <div className="right-banner"/>
                        </Grid>
                    </Hidden>
                </Grid>
            </Container>
        </MainLayout>
    )
}
