import React, {FC, useEffect, useState} from 'react';
import {useSelector, useDispatch} from "react-redux";
import {i18n} from "@root/i18n";
import {userAPI} from '@src/api/api';
import {CreateAncmntAuction} from "./CreateAncmntAuction";
import {RootState} from "@src/redux/rootReducer";
import {MainLayout} from "@src/components/MainLayout";
import {CameraIcon} from "@src/components/elements/icons";
import {
    FileType,
    CreateAncmntState,
    AncmntTypesState, AncmntType
} from "@root/interfaces/Announcement";
import {AncmntAuctionFormContainer} from "./ancmnt_auction_form/AncmntAuctionFormContainer";
import {setErrorMsgAction} from '@root/src/redux/slices/errorSlice';
import {AncmntTypesPage} from "@src/components/announcement_auction/ancmnt_types_page/AncmntTypesPage";
import {WithT} from "i18next";
import {categoryDataNormalization, categorySearchHelper} from "@src/helpers";
import {AncmntAuctionTop} from './ancmnt_auction_top/AncmntAuctionTop';
import {SuccessAncmntAuction} from "@src/components/announcement_auction/create_ancmnt_auction/ancmnt_auction_form/success_ancmnt_auction/SuccessAncmntAuction";
import {setFetchedData} from "@src/helpers";


export const initUrl: FileType = {
    url: (
        <div style={{width: '140px', height: '140px', margin: 'auto'}}>
            <CameraIcon/>
        </div>
    )
};

const steps = ['Тип объявления', 'Категория', 'Заполнение', 'Проверка'];

const initAncmntTypes = {
    isFetch: false,
    types: [
        {
            id: 1,
            name: "Обычный",
            currency: [
                {
                    id: 1,
                    name: "уе"
                },
                {
                    id: 2,
                    name: "sum"
                }
            ],
            expired: [
                {
                    id: 3,
                    expiration_at: 720
                }
            ],
            image: {
                url: '/img/adv-background.png'
            }
        },
        {
            id: 2,
            name: "Аукцион",
            currency: [
                {
                    id: 3,
                    name: "sum"
                }
            ],
            expired: [
                {
                    id: 1,
                    expiration_at: 2
                },
                {
                    id: 2,
                    expiration_at: 720
                }
            ],
            image: {
                url: '/img/lot-background.png'
            }
        },
        {
            id: 3,
            name: "Продвинутый аукцион",
            currency: [
                {
                    id: 4,
                    name: "sum"
                }
            ],
            expired: [
                {
                    id: 4,
                    expiration_at: 2
                },
                {
                    id: 5,
                    expiration_at: 720
                }
            ],
            image: {
                url: '/img/advanced-lot-background.png'
            }
        }
    ]
};

export const CreateAncmntAuctionContainer: FC<WithT> = ({t}) => {
    const lang = i18n.language;

    const initStatuses = {
        isForm: false,
        isPreview: false,
        isSuccess: false
    };

    const initAncmntType = {
        id: null,
        name: '',
        currency: [{
            id: null,
            name: ''
        }],
        expired: [{
            id: null,
            expiration_at: null
        }],
        image: {
            url: ''
        }
    };

    const initCreateAncmntState: CreateAncmntState = {
        isFetch: false,
        error: null,
        category: {
            id: null,
            name: '',
            images: {
                id: null,
                url: {
                    default: ''
                }
            },
            icons: {
                id: null,
                url: {
                    default: ''
                }
            },
            childs: [],
            has_auction: null,
        },
        subCategory: {
            id: null,
            name: '',
            data: {}
        }
    };

    const categoriesList = useSelector(({categories}: RootState) => categoryDataNormalization(categories.list));
    const dispatch = useDispatch();

    const [isPreview, setIsPreview] = useState(false);

    const [ancmntTypes, setAncmntTypes] = useState<AncmntTypesState>(initAncmntTypes);

    const [ancmntType, setAncmntType] = useState<AncmntType>(initAncmntType);
    const isAncmntTypeSelected = ancmntType.id !== null;

    const [createAncmnt, setCreateAncmnt] = useState<CreateAncmntState>(initCreateAncmntState);
    const {category, subCategory} = createAncmnt;

    const [activeStep, setActiveStep] = useState(0);

    const [searchTxt, setSearchTxt] = useState('');

    const handleNextStep = () => {
        setActiveStep((prevStep) => prevStep + 1);
    };

    const handlePrevStep = () => {
        setActiveStep((prevStep) => prevStep - 1);
    };

    const handleResetSteps = () => {
        setActiveStep(0);
    };

    // const setAncmntsTypes = async () => {
    //     try {
    //         const types = userAPI.getAncmntsTypes(lang);
    //         await setFetchedData(
    //             ancmntTypes,
    //             setAncmntTypes,
    //             {types},
    //         );
    //     } catch (e) {
    //         dispatch(setErrorMsgAction(e.message));
    //         setAncmntTypes({...ancmntTypes, isFetch: false});
    //     }
    // };

    const handleAncmntType = (selectedAncmntType) => () => {
        handleNextStep();
        setAncmntType(selectedAncmntType);
    };

    const handleSearch = ({target}) => {
        setSearchTxt(target.value);
    };

    const setFoundCategoriesChilds = () => {
        const childs = !!searchTxt
            ? categorySearchHelper(searchTxt, categoriesList)
            : [];

        setCreateAncmnt({
            ...createAncmnt,
            category: {
                ...createAncmnt.category,
                ...initCreateAncmntState.category,
                childs
            }
        });
    };

    const handleCategory = (category) => async () => {
        try {
            if (category.id === 11 || category.id === 12) {
                setCreateAncmnt({...createAncmnt, category});
                handleNextStep();
            } else {
                setCreateAncmnt({
                    ...createAncmnt,
                    category
                });
            }
        } catch (e) {
            dispatch(setErrorMsgAction(e.message));
            setCreateAncmnt({...createAncmnt, isFetch: false});
        }
    };

    const handleSubCategory = (parent, child_id, name) => async () => {
        try {
            const data = userAPI.getAdDataForCreateAncmnt(parent.id, child_id, lang);
            await setFetchedData(
                createAncmnt,
                setCreateAncmnt,
                {
                    subCategory: {
                        id: child_id,
                        name,
                        data
                    }
                }
            );
            handleNextStep();
        } catch (e) {
            dispatch(setErrorMsgAction(e.message));
            setCreateAncmnt({...createAncmnt, isFetch: false});
        }
    };

    const handleCreateReset = () => {
        setSearchTxt('');
        setCreateAncmnt(initCreateAncmntState);
    };

    const handleResetAncmntType = () => {
        handleResetSteps();
        handleCreateReset();
        setAncmntType(initAncmntType);
    };

    const handleBackBtn = () => {
        handlePrevStep();
        switch (activeStep) {
            case 1:
                handleResetAncmntType();
                break;
            case 2:
                handleCreateReset();
                break;
            case 3:
                setIsPreview(false);
        }
    };

    const setCreateAncmntByLang = async () => {
        const [currentCtgr] = categoriesList.filter(ctgr => ctgr.id === category.id);
        console.log('current:', currentCtgr)
        currentCtgr && setCreateAncmnt({
            ...createAncmnt,
            category: currentCtgr,
        });
        if (subCategory.id !== null) {
            try {
                const data = userAPI.getAdDataForCreateAncmnt(category.id, subCategory.id, lang);
                await setFetchedData(
                    createAncmnt,
                    setCreateAncmnt,
                    {
                        subCategory: {
                            ...subCategory,
                            data
                        }
                    }
                );
            } catch (e) {
                dispatch(setErrorMsgAction(e.message));
                setCreateAncmnt({...createAncmnt, isFetch: false});
            }
        }
    };

    const renderPageByActiveStep = () => {
        switch (activeStep) {
            case 0:
                return <AncmntTypesPage
                    ancmntTypes={ancmntTypes}
                    handleAncmntType={handleAncmntType}
                />
            case 1:
                return <CreateAncmntAuction
                    ancmntType={ancmntType}
                    createAncmnt={createAncmnt}
                    categoriesList={categoriesList}
                    searchTxt={searchTxt}
                    handleSearch={handleSearch}
                    handleCategory={handleCategory}
                    handleSubCategory={handleSubCategory}
                />
            case 2:
                return <AncmntAuctionFormContainer
                    t={t}
                    isPreview={isPreview}
                    ancmntType={ancmntType}
                    createAncmnt={createAncmnt}
                    setIsPreview={setIsPreview}
                    handleNextStep={handleNextStep}
                />
            case 3:
                return <SuccessAncmntAuction
                    handleCreateNewAncmnt={handleResetAncmntType}
                />
        }
    };

    // useEffect(() => {
    //    setAncmntsTypes();
    // }, []);

    useEffect(() => {
        setCreateAncmntByLang();
    }, [categoriesList[0].name]);

    useEffect(() => {
        setFoundCategoriesChilds();
    }, [searchTxt]);

    console.log(createAncmnt)
    console.log(ancmntTypes)
    return (
        <MainLayout>
            {activeStep !== 0
            && <AncmntAuctionTop
                steps={steps}
                activeStep={activeStep}
                handleBackBtn={handleBackBtn}
            />}
            {renderPageByActiveStep()}
        </MainLayout>
    )
}
