import {makeStyles} from '@material-ui/core/styles'

export const useStyles = makeStyles(() => ({
    root: {
        marginTop: '15px',
    },
    gridItem: {
        display: props => props.isPreview && 'flex',
        alignItems: props => props.isPreview && 'center',
        '& > h6': {
            marginRight: '10px'
        },
    }
}))
