import React from "react"
import {Formik} from "formik"
import {useRouter} from "next/router"
import {Typography} from "@material-ui/core"
import {ButtonComponent} from "@src/components/elements/button/Button"
import {useStyles} from './useStyles'

const initValues = {
    type: 'Выберите тип объявления',
    category: ''
};

export const CreateAdModalForm = (props) => {
    const {handleCloseModal} = props;

    const router = useRouter();
    const forward = () => {
        router.push('/create_advertisement');
    };

    const submit = () => {
        forward();
    };

    const classes = useStyles();
    return (
        <Formik initialValues={initValues} onSubmit={submit}>
            {({errors, touched, setFieldValue}) => {
                return (
                    <form className={classes.root}>
                        <div className='form-btns'>
                            <ButtonComponent onClick={handleCloseModal}>
                                <Typography>Отмена</Typography>
                            </ButtonComponent>
                            <ButtonComponent type='submit'>
                                <Typography>Продолжить</Typography>
                            </ButtonComponent>
                        </div>
                    </form>
                )
            }}
        </Formik>
    )
};
