import {createAsyncThunk, createSlice} from '@reduxjs/toolkit'
import {userAPI} from '@src/api/api'


const fetchToken = createAsyncThunk<any>(
    'auth/fetchByIdStatus',
    async (phone, password) => {
        const response = await userAPI.login(phone, password)
        return response.data
    }
)

export type AuthTypes = {
    isFetch: boolean,
    isAuth: boolean,
    error?: any
};

const initialState: AuthTypes = {
    isFetch: false,
    isAuth: false,
    error: null
};

const authSlice = createSlice({
    name: 'auth',
    initialState,
    reducers: {},
    extraReducers: {
        [fetchToken.pending.type]: (state) => {
            state.isFetch = true;
            state.error = null;
        },
        [fetchToken.fulfilled.type]: (state, action) => {
            state.isFetch = false;
            state.isAuth = action.payload;
        },
        [fetchToken.rejected.type]: (state, action) => {
            state.isFetch = false;
            state.error = action.payload;
        }
    }
});

export default authSlice.reducer;
