import React from 'react'
import {Typography} from '@material-ui/core'
import {CarIcon} from '@src/components/elements/icons'
import {CustomSlider} from "@src/components/elements/custom_slider/CustomSlider"
import {settings} from './sliderSettings'
import {useSelector} from 'react-redux'
import {RootState} from "@src/redux/rootReducer"
import {useStyles} from './useStyles'
import {CustomImageTag} from "@src/components/elements/custom_image_tag/CustomImageTag";


export const CategoriesSlider = (props) => {
    const {t} = props;

    const {categories} = useSelector(({categories}: RootState) => categories);

    const classes = useStyles();
    return (
        <div className={classes.root}>
            <Typography variant="h4">{t('popularCategories')}</Typography>
            <div className='category-slider'>
                <CustomSlider {...settings}>
                    {
                        categories.map(category => (
                            <a href="#">
                                <CustomImageTag src={CarIcon} alt='icon'/>
                                <Typography>Легковые автомобили</Typography>
                            </a>
                        ))
                    }
                </CustomSlider>
            </div>
        </div>
    )
};
