import React, {FC} from "react";
import {Autocomplete} from "@material-ui/lab";
import {TextField} from "@material-ui/core";
import {AddressAutocompleteProps} from "@root/interfaces/AddressAutocomplete";


const option = ({area, city, district}) => {
    return `${district ? `${district}, ` : ''}${city}, ${area}`;
};

const formatData = (list) => {
    return list.reduce((acc, row) => {
        row.cities.forEach((city) => {
            if (city.district.length) {
                city.district.forEach((dis) => {
                    acc = [
                        ...acc,
                        {
                            area: row.name,
                            city: city.name,
                            district: dis.name
                        }
                    ];
                });
            } else {
                acc = [
                    ...acc,
                    {
                        area: row.name,
                        city: city.name
                    }
                ];
            }
        });
        return acc;
    }, []);
};

const optionSelected = ({district, city}, value) => {
    return value.district.id ? district.id === value.district.id : city.id === value.city.id;
};

export const AddressAutocomplete: FC<AddressAutocompleteProps> = ({setValues, ...props}) => {
    return (
        <Autocomplete
            noOptionsText='Город или регион с таким именем не найден'
            options={formatData(props.list)}
            getOptionSelected={optionSelected}
            getOptionLabel={option}
            renderInput={(params) => {
                console.log(params)
                return <TextField
                    fullWidth
                    focused={false}
                    variant='outlined'
                    {...params}
                    {...props}
                />
            }}
        />
    )
};
