import {AnyAction} from "redux"
import {createReducer} from '@reduxjs/toolkit'
import {fetchTokenBegin, fetchTokenSuccess, fetchTokenFailure} from '../actions/authActions'

export type AuthTypes = {
    isFetch: boolean,
    isAuth: boolean,
    error?: any
};

const initialState = {
    isFetch: false,
    isAuth: false,
    error: null
};

export const authReducer = createReducer(initialState, {
        [fetchTokenBegin]: (state, action) => ({...state, isAuth: action.payload}),
        [fetchTokenBegin]: (state) => ({
                ...state,
                isFetch: true,
                error: null
            }),
        [fetchTokenSuccess]: (state, action) => ({
                ...state,
                isFetch: false,
                ...action.payload
            }),
        [fetchTokenFailure]: (state, action) => ({
                ...state,
                isFetch: false,
                error: action.payload
            })
});
