import {makeStyles} from '@material-ui/core/styles'

export const useStyles = makeStyles((theme) => ({
    root: {
        '& div.advrt-banner': {
            marginTop: '140px',
            '& > div': {
                width: '100%',
                height: '470px',
                borderRadius: '7px',
                backgroundColor: '#C0C0C0',
            },
        },
        '& button.back-btn': {
            width: '250px',
            margin: '50px 5px 0',
            color: theme.palette.primary.white
        },
        '& div.categories-block': {
            marginTop: '20px'
        }
    },
    '& ul.categories-list': {
        '& > li > div': {
            cursor: 'pointer'
        }
    }
}))
