import {makeStyles} from '@material-ui/core/styles'


export const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
        width: '400px',
        height: '300px',
        backgroundColor: theme.palette.primary.white,
        '& button': {
            padding: '20px 0',
            borderRadius: '2px'
        },
        '& > div.form-btns': {
            display: 'flex',
            '& > button': {
                margin: '0 4px'
            }
        }
    },
    menu: {}
}))
