import {object, number, string, array, lazy} from "yup";


export const requiredFields = {
    year: true,
    mileage: true,
    body: true,
    manufacturer: true,
    model: true,
    colors: true,
    car_engine_type: true,
    transmission: true,
};

const requiredMsg = 'Обязательный параметр!';

const lazyAdParams = lazy((value) => object(
    Object.entries(value)
        .reduce(
            (a, [key]) => {
                if (requiredFields[key]) {
                    a[key] = object({
                        id: number().nullable().required(requiredMsg)
                    });
                    return a;
                }
            }, {}
        )
));

export const createAdvrtSchema = object().shape({
    adType: object({
        id: number().nullable().required(requiredMsg)
    }),
    category: object({
        id: number().nullable().required(requiredMsg)
    }),
    title: string().required(requiredMsg),
    price: string().required(requiredMsg),
    currency: lazy(value => {
        if (value) {
            return string().required(requiredMsg)
        }
    }),
    location: object({
        city_id: number().required(requiredMsg),
    }).nullable().required(requiredMsg),
    files: array().required(requiredMsg),
    description: string().required(requiredMsg),
    phone: string().required(requiredMsg),
    adParams: lazyAdParams
});
